                "no-extra-boolean-cast": 1, //多余的感叹号转布尔型
                "no-extra-semi": 1, //多余的分号
                "no-extra-parens": 0, //多余的括号
                "no-empty": 1, //空代码块

                //使用前未定义
                "no-use-before-define": [
                        0,
                        "nofunc"
                ],

                "complexity": [0, 10], //圈复杂度大于*

